import React from "react";
// TODO: import useFormik from formik library
import{useFormik} from 'formik';
function App() {
  // TODO: add a const called formik assigned to useFormik()
  const formik = useFormik({
    initialValues: {
      username : '',
      password : ''
    },
    onSubmit: values => {
      console.log('form: ', values)
    },
    validate: values => {
      let errors = {};
      if(!values.username) errors.username = 'Field Required';
      else if (
        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(
          values.username
        )
      ) {
        errors.username = 'Invalid email address';
      }
      if(!values.password) errors.password = 'Field Required';
      return errors;
    }
  });

  return (
    <div>
       <form onSubmit = {formik.handleSubmit}>
        <div>Username</div>
        <input name = "username" id = "emailField" type = "text" onChange={formik.handleChange} value = {formik.values.username}></input>
        {formik.errors.username ? <div id = {{emailError}} style = {{color:'red'}}>{formik.errors.username}</div>: null}
        <div>Password</div>
        <input name = "password" id = "pswField" type = "text" onChange={formik.handleChange} value = {formik.values.password}></input>
        {formik.errors.password ? <div id = {{pswError}} style = {{color:'red'}}>{formik.errors.password}</div>: null}
        <button id = "submitBtn" type = "submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
